import React from "react";
import { GetServerSideProps } from "next";
import ReactMarkdown from "react-markdown";
import Layout from "../../components/Layout";
import Router from "next/router";
import { PostProps } from "../../components/Post";
import { useSession } from "next-auth/client";
import prisma from "../../lib/prisma";
import Link from "next/link";

export const getServerSideProps: GetServerSideProps = async ({ params }) => {
  const postR = await prisma.post.findUnique({
    where: {
      id: Number(params?.id) || -1,
    },
    include: {
      author: {
        select: { name: true, email: true },
      },
    },
  });
  const post = JSON.parse(JSON.stringify(postR));
  return {
    props: post,
  };
};

async function publishPost(id: number): Promise<void> {
  await fetch(`/api/publish/${id}`, {
    method: "PUT",
  });
  await Router.push("/");
}

async function deletePost(id: number): Promise<void> {
  await fetch(`/api/post/${id}`, {
    method: "DELETE",
  });
  Router.push("/");
}

async function editPost(id: number): Promise<void> {
  Router.push(`/edit/${id}`);
}

const Post: React.FC<PostProps> = (props) => {
  const [session, loading] = useSession();
  if (loading) {
    return <div>Authenticating ...</div>;
  }
  const userHasValidSession = Boolean(session);
  const postBelongsToUser = session?.user?.email === props.author?.email;
  let title = props.title;
  if (!props.published) {
    title = `${title} (Draft)`;
  }

  return (
    <Layout>
      <div>
        <div className="postContents">
          <h2>{title}</h2>
          <p>By {props?.author?.name || "Unknown author"}</p>
          <ReactMarkdown source={props.content} />
          <small>
            Created at: {props.createdAt.substring(0, 10)}{" "}
            {props.createdAt.substring(11, 19)}{" "}
          </small>
          <br />
          <small>
            Updated at: {props.updatedAt.substring(0, 10)}{" "}
            {props.updatedAt.substring(11, 19)}
          </small>
          <br />
        </div>
        {!props.published && userHasValidSession && postBelongsToUser && (
          <button onClick={() => publishPost(props.id)}>Publish</button>
        )}
        {userHasValidSession && postBelongsToUser && (
          <>
            <button onClick={() => editPost(props.id)}>Edit</button>
            <button onClick={() => deletePost(props.id)}>Delete</button>
          </>
        )}
      </div>
      <style jsx>{`
        .page {
          background: rgba(0, 255, 0, 0.05);
          padding: 2rem;
        }

        .actions {
          margin-top: 2rem;
        }

        .postContents {
          background: white;
          margin-bottom: 2rem;
        }

        button {
          background: #00e600;
          /* border: 0; */
          border: 1px solid black;
          border-radius: 0.125rem;
          padding: 1rem 2rem;
        }

        button + button {
          margin-left: 1rem;
        }
      `}</style>
    </Layout>
  );
};

export default Post;
