import React, { useState, useEffect } from "react";
import Link from "next/link";
import Router, { useRouter } from "next/router";
import { signOut, useSession } from "next-auth/client";
import { Search } from "@material-ui/icons";
import prisma from "../lib/prisma";
import Post, { PostProps } from "../components/Post";

const Header: React.FC = () => {
  const router = useRouter();
  const isActive: (pathname: string) => boolean = (pathname) =>
    router.pathname === pathname;

  const [session, loading] = useSession();
  const [searching, setSearching] = useState(false);
  const [searchKeyword, setSearchKeyword] = useState("");
  const [resultUsers, setResultUsers] = useState([]);
  const [resultPosts, setResultPosts] = useState([]);

  useEffect(() => {
    console.log("reset keyword");
    setSearching(false);
    setSearchKeyword("");
  }, [router.pathname]);

  // console.log("router.pathname: " + router.pathname);

  useEffect(() => {
    if (searchKeyword) {
      const resultUsers = prisma.user.findMany({
        where: {
          name: {
            contains: searchKeyword,
          },
        },
      });
      const resultPosts = prisma.post.findMany({
        where: {
          AND: {
            title: {
              contains: searchKeyword,
            },
            content: {
              contains: searchKeyword,
            },
          },
        },
      });
    } else {
      setResultUsers([]);
      setResultPosts([]);
    }
  }, [searchKeyword]);

  let left = (
    <div className="left">
      <Link href="/">
        <a className="bold" data-active={isActive("/")}>
          Feed
        </a>
      </Link>

      <a className="search" onClick={() => setSearching(!searching)}>
        <Search />
      </a>

      <style jsx>{`
        .bold {
          font-weight: bold;
        }

        a {
          text-decoration: none;
          color: #000;
          display: inline-block;
        }

        .left a[data-active="true"] {
          color: gray;
        }

        a + a {
          margin-left: 1rem;
        }
      `}</style>
    </div>
  );

  let right = null;

  if (loading) {
    left = (
      <div className="left">
        <Link href="/">
          <a className="bold" data-active={isActive("/")}>
            Feed
          </a>
        </Link>
        <style jsx>{`
          .bold {
            font-weight: bold;
          }

          a {
            text-decoration: none;
            color: #000;
            display: inline-block;
          }

          .left a[data-active="true"] {
            color: gray;
          }

          a + a {
            margin-left: 1rem;
          }
        `}</style>
      </div>
    );
    right = (
      <div className="right">
        <p>Validating session ...</p>
        <style jsx>{`
          .right {
            margin-left: auto;
          }
        `}</style>
      </div>
    );
  }

  if (!session) {
    right = (
      <div className="right">
        <Link href="/api/auth/signin">
          <a data-active={isActive("/signup")}>Log in</a>
        </Link>
        <style jsx>{`
          a {
            text-decoration: none;
            color: #000;
            display: inline-block;
            background: #00e600;
          }

          a + a {
            margin-left: 1rem;
          }

          .right {
            margin-left: auto;
          }

          .right a {
            border: 1px solid black;
            padding: 0.5rem 1rem;
            border-radius: 3px;
          }
        `}</style>
      </div>
    );
  }

  if (session) {
    left = (
      <div className="left">
        <Link href="/">
          <a className="bold" data-active={isActive("/")}>
            Feed
          </a>
        </Link>
        <Link href="/drafts">
          <a data-active={isActive("/drafts")}>My drafts</a>
        </Link>

        <a className="search" onClick={() => setSearching(!searching)}>
          <Search />
        </a>

        <style jsx>{`
          .bold {
            font-weight: bold;
          }

          a {
            text-decoration: none;
            color: #000;
            display: inline-block;
          }

          .left a[data-active="true"] {
            color: gray;
          }

          a + a {
            margin-left: 1rem;
          }
        `}</style>
      </div>
    );
    right = (
      <div className="right">
        <p>
          {session.user.name} ({session.user.email})
        </p>
        {!session.user.email && (
          <Link href="/update-user">
            <button>
              <a>Register Email</a>
            </button>
          </Link>
        )}
        {session.user.email && (
          <>
            <Link href="/update-user">
              <button>
                <a>Update Email</a>
              </button>
            </Link>
            <Link href="/create">
              <button>
                <a>New post</a>
              </button>
            </Link>
          </>
        )}
        <button onClick={() => signOut()}>
          <a>Log out</a>
        </button>
        <style jsx>{`
          a {
            text-decoration: none;
            color: #000;
            display: inline-block;
          }

          p {
            display: inline-block;
            font-size: 13px;
            padding-right: 1rem;
          }

          a + a {
            margin-left: 1rem;
          }

          .right {
            margin-left: auto;
          }

          .right a {
            /* border: 1px solid black; */
            border: none;
            padding: 0.5rem 1rem;
            /* border-radius: 3px; */
          }

          button {
            /* border: none; */
            border: 1px solid black;
            background: #00e600;
            border-radius: 3px;
            margin-left: 0.5rem;
          }
        `}</style>
      </div>
    );
  }

  return (
    <>
      <nav>
        {left}
        {right}
        <style jsx>{`
          nav {
            display: flex;
            padding: 2rem;
            align-items: center;
          }
        `}</style>
      </nav>
      {searching && (
        <>
          <div>
            <input
              autoFocus
              onChange={(e) => setSearchKeyword(e.target.value)}
              placeholder="Search for users and posts."
              type="text"
              value={searchKeyword}
            />
          </div>
          <style jsx>{`
            input[type="text"] {
              width: 100%;
              padding: 0.5rem;
              margin: 0.5rem 0;
              border-radius: 0.25rem;
              border: 0.125rem solid rgba(0, 0, 0, 0.2);
            }
          `}</style>
        </>
      )}
    </>
  );
};

export default Header;
