import prisma from "../../../lib/prisma";

// PUT /api/publish/:id
export default async function handle(req, res) {
  const email = req.query.email;
  const userId = req.query.userId;
  const user = await prisma.user.update({
    where: { id: Number(userId) },
    data: { email: email },
  });
  res.json(user);
}
