import React, { useState } from "react";
import Layout from "../components/Layout";
import Router from "next/router";
import { useSession, getSession } from "next-auth/client";
import { GetServerSideProps } from "next";
import prisma from "../lib/prisma";
import { Prisma } from "@prisma/client";

export type UserProps = {
  id: number;
  name: string | null;
  email: string | null;
  posts: {
    id: number;
    title: string;
    content: string;
  };
};

export const getServerSideProps: GetServerSideProps = async ({ req, res }) => {
  const session = await getSession({ req });

  // console.log("session: " + JSON.stringify(session, null, 2));

  if (!session) {
    res.statusCode = 403;
    return { props: { user_id: null } };
  }

  let token: string;
  if (typeof session.accessToken == "string") {
    token = session.accessToken;
  } else {
    token = "";
  }

  const targetSession = await prisma.session.findUnique({
    where: { accessToken: token },
  });
  // console.log(targetSession.userId);
  const userId = targetSession.userId;

  const targetUser = await prisma.user.findUnique({
    where: { id: userId },
  });
  const userEmail = targetUser.email;
  return { props: { user_id: userId, userEmail: userEmail } };
};

type Props = {
  user_id: number;
  userEmail: string | null;
};

const UpdateUser: React.FC<Props> = ({ user_id, userEmail }) => {
  const [userId, setUserId] = useState(user_id);
  const [email, setEmail] = useState(userEmail);

  console.log("userEmail: " + userEmail);

  // console.log("props.userId: " + props.userId);

  const submitData = async (e: React.SyntheticEvent) => {
    e.preventDefault();
    try {
      const body = { email };
      await fetch("/api/post", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });
      await Router.push("/");
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <Layout>
      <div>
        <h1>Update email of the user whose id is {userId}.</h1>
        <form onSubmit={submitData}>
          <input
            autoFocus
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Title"
            type="text"
            value={email}
          />
          <input disabled={!email} type="submit" value="Register" />
        </form>
      </div>
    </Layout>
  );
};

export default UpdateUser;
