import React, { useState } from "react";
import { GetServerSideProps } from "next";
import Layout from "../../components/Layout";
import Router from "next/router";
import prisma from "../../lib/prisma";
import { PostProps } from "../../components/Post";
import { stringify } from "querystring";

export const getServerSideProps: GetServerSideProps = async ({ params }) => {
  console.log("keyword" + params?.keyword);
  const searchKeyword: string = params?.keyword.toString();

  const postR = await prisma.post.findMany({
    where: {
      AND: {
        title: {
          contains: searchKeyword,
        },
        content: {
          contains: searchKeyword,
        },
      },
    },
    include: {
      author: {
        select: { name: true, email: true },
      },
    },
  });
  const post = JSON.parse(JSON.stringify(postR));
  return {
    props: post,
  };
};

const Search: React.FC<PostProps> = (props) => {
  return (
    <Layout>
      <div>
        <p>search page</p>
      </div>

      <style jsx>{`
        .page {
          background: white;
          padding: 3rem;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        input[type="text"],
        textarea {
          width: 100%;
          padding: 0.5rem;
          margin: 0.5rem 0;
          border-radius: 0.25rem;
          border: 0.125rem solid rgba(0, 0, 0, 0.2);
        }

        input[type="submit"] {
          background: #00e600;
          /* border: 0; */
          border: 1px solid black;
          border-radius: 3px;
          padding: 1rem 2rem;
        }

        .back {
          margin-left: 1rem;
        }
      `}</style>
    </Layout>
  );
};

export default Search;
